update.Script:
var Total =[Cantidad_Total]/[Cantidad_Por_Unidad_Entrega]


if ([Cantidad_Total]=null)
{ 
  setTimeout(function () {
      $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
	}, 50);

}
else
{
      if([Cantidad_Por_Unidad_Entrega]= null)
      {
         setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
          }, 50);
      }
        else
        {
          setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', Total);//Estableciendo valor
          }, 50);
        }

}

if ([Cantidad_Total]=null)
{ 
  setTimeout(function () {
      $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
	}, 50);

}
else
{
      if([Cantidad_Por_Unidad_Entrega]= null)
      {
         setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
          }, 50);
      }
        else
        {
          var Total =[Cantidad_Total]/[Cantidad_Por_Unidad_Entrega]
          setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', Total);//Estableciendo valor
          }, 50);
        }

}

if ([Cantidad_Total]=null){ 
  setTimeout(function () {
      $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
	}, 50);

}
else{
      if([Cantidad_Por_Unidad_Entrega]= null){
         setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor
          }, 50);
      }
        else{
          var Total =[Cantidad_Total]/[Cantidad_Por_Unidad_Entrega]
          setTimeout(function () {
            $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', Total);//Estableciendo valor
          }, 50);
        }

}

setTimeout(function () {
    if ([Cantidad_Total]=null){ 

          $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor


    }
    else{
          if([Cantidad_Por_Unidad_Entrega]= null){

                $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', "0");//Estableciendo valor

          }
            else{
              var Total =[Cantidad_Total]/[Cantidad_Por_Unidad_Entrega]

                $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', Total);//Estableciendo valor

            }

    }
}, 50);

