var Total =[Cantidad_Total]/[Cantidad_Por_Unidad_Entrega]
if([Cantidad_Total]== null || [Cantidad_Total]== 0){
 $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', '0');//Estableciendo valor
}
else
{
  if([Cantidad_Por_Unidad_Entrega]== null || [Cantidad_Por_Unidad_Entrega]== 0)
  {
     $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', '0');//Estableciendo valor

  }

  else{
        setTimeout(function () {
        $app.touch.contextDataView().updateFieldValue('Cantidad_Entrega', Total);//Estableciendo valor
        }, 50);
      }

}

