DECLARE @Clave_ INT SET @Clave_ = ISNULL(( SELECT TOP 1 Clave_Tipo_Ubicacion FROM APP.Tipo_Ubicacion WHERE Tipo_Ubicacion_Id = @Devolucion_Ubicacion_Origen_Id_ ),0)
DECLARE @Material_Stock_Total_Piso_ NUMERIC(15,4) SET @Material_Stock_Total_Piso_ = ISNULL(( SELECT TOP 1 SUM(mus_.Total_Stock) FROM [APP].[Material_Ubicacion_Stock] mus_ INNER JOIN APP.Tipo_Ubicacion tu ON ( mus_.Tipo_Ubicacion_Id = tu.Tipo_Ubicacion_Id AND tu.Es_Piso = 1 AND tu.Clave_Tipo_Ubicacion = @Clave_ AND tu.Estatus_Registro_Id = 1 ) AND mus_.Material_Id = @Devolucion_Material_Id AND mus_.Tipo_Ubicacion_Id = @Devolucion_Ubicacion_Origen_Id_ AND mus_.Estatus_Registro_Id = 1 ),0);
IF @Material_Stock_Total_Piso_ < @Cantidad_A_Devolver BEGIN --SET @Devolucion_Material_Id = NULL SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = 'La cantidad a devolver debe ser menor o igual a la cantidad de existencia en piso. La cantidad Actual es ' + CONVERT(VARCHAR(30),CONVERT(NUMERIC(15,4),@Material_Stock_Total_Piso)) --SET @Result_ShowAlert = @Result_ShowViewMessage RETURN END */

