 setTimeout(function () {
 $app.touch.contextDataView().updateFieldValue('Cantidad_De_Etiquetas', 0);//Estableciendo valor
 }, 50);
}
else
{
  if([Cantidad_De_Bultos]== null || [Cantidad_De_Bultos]== 0)
  {
     setTimeout(function () {
     $app.touch.contextDataView().updateFieldValue('Cantidad_De_Etiquetas', 0);//Estableciendo valor
     }, 50);

  }

  else{
        setTimeout(function () {
        $app.touch.contextDataView().updateFieldValue('Cantidad_De_Etiquetas', Total);//Estableciendo valor
        }, 50);
      }

}

