DECLARE @Cus_Lin INT; DECLARE @Cus_Ord BIGINT;
SELECT TOP 1 @Producto_Descripcion= P.Producto_Descripcion ,@Cantidad_Total = OP.Cantidad --,@Cantidad_Total = OP.Cantidad_Total FROM dbo.Orden_Produccion OP INNER JOIN APP.Producto P ON OP.Producto_Id = P.Producto_Id WHERE OP.Orden_Produccion_Id = @Orden_Produccion_Id --- SELECT TOP 1 @DC_S_Descripcion = PL.Descripcion_Planta ,@Cus_Lin = PL.Cus_Lin ,@Cus_Ord = PL.Cus_Ord FROM dbo.Orden_Produccion OP INNER JOIN APP.Planta PL ON OP.DC_S_Planta_Id = PL.Planta_Id WHERE OP.Orden_Produccion_Id = @Orden_Produccion_Id ---------------------------------------------------------
IF @Cus_Lin IS NULL AND @Cus_Ord IS NULL BEGIN SET @S_O = ''; END ELSE BEGIN SET @S_O = CONCAT(@Cus_Ord, ' / ', RIGHT('00000' + CONVERT(VARCHAR(10),@Cus_Lin), 6)) -- SO END

