Mat_Ubicacion = CONVERT(VARCHAR(MAX),ISNULL(( SELECT STUFF(( SELECT '; Ubicación: ' + v.Ubicacion_Almacenamiento + ', Stock: ' + CONVERT(VARCHAR(20),CONVERT(NUMERIC(15,1),v.Total_Stock)) FROM [dbo].[Mat_Ubic_Almacen_Mostrar_En_Solic_Manual_V] v WHERE v.Material_Id = @Material_Solicitado_Id FOR XML PATH('') ), 1, 1, '') ),'N/A') )

