DECLARE @Already_Exists BIT
SET @Already_Exists = 0
SELECT TOP 1 @Already_Exists = 1 FROM APP.Area_Servicio_Familia WHERE Area_Servicio_Id = @Area_Servicio_Id AND Familia_Id = @Familia_Id AND Estatus_Registro_Id = @Estatus_Registro_Id AND Area_Servicio_Familia_Id <> @Area_Servicio_Familia_Id
IF @Already_Exists = 1 BEGIN -- Si existe otro registro que coincide con los datos (pero puede estar repetido por la reutilización de la tabla), entonces permititmos editar DECLARE @B_Area_Servicio_Id INT, @B_Familia_Id INT SELECT TOP 1 @B_Area_Servicio_Id = Area_Servicio_Id ,@B_Familia_Id = Familia_Id FROM APP.Area_Servicio_Familia WHERE Area_Servicio_Familia_Id = @Area_Servicio_Familia_Id
-- Validar si cambio algun otro campo IF(@B_Area_Servicio_Id <> @Area_Servicio_Id OR @B_Familia_Id <> @Familia_Id) BEGIN -- El usuario actualizó los valores principales y si se encontró otro registro, entonces no se debe permitir la edición SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = [dbo].[fn_Obtener_Mensaje_Sistema](1565,1,45,@Area_Servicio + ' vs. ' + @Familia_Descripcion ) SET @Result_ShowAlert = @Result_ShowViewMessage RETURN END ELSE IF (@Estatus_Registro_Id = 1) BEGIN -- El usuario cambio el Estatus de registro a activo y ya hay otro registro con misma area, familia y activo, entonces ya no se permite acticvar SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = [dbo].[fn_Obtener_Mensaje_Sistema](1565,1,45,@Area_Servicio + ' vs. ' + @Familia_Descripcion ) SET @Result_ShowAlert = @Result_ShowViewMessage RETURN
END -- DE lo contrario si se permite actualizar
END -- ELSE SI SE PERMITE EDITAR
