businessRules.rule:
var fieldValue = [Total_Stock_En_Ubicacion_Almacenamiento_Seleccionado];
var success = this.validateInput();
var commandName = this.arguments().CommandName;
var triggerFieldName = this.arguments().Trigger;
var tryConversion = triggerFieldName == 'Cantidad_Entregado';

if (fieldValue != null && tryConversion) {
  success = fieldValue > [Cantidad_Entregado];
	if (!success){		
    	this.preventDefault();  	
  		this.result.focus('Cantidad_Entregado','El stock en la ubicación seleccionada es menor a cantidad entregado.')        
	}
}

DECLARE @Cantidad_A_Entregar_Es_Valido BIT = 0;
DECLARE @Mensaje_Id INT = 0;

DECLARE @Cantidad_Solicitado NUMERIC(15,4) = 0;
DECLARE @Cantidad_Entregado_Ubicacion_Ruta_Optima NUMERIC(15,4) = 0;
DECLARE @Cantidad_Entregado_Otra_Ubicacion NUMERIC(15,4) = 0;

SET @Cantidad_Solicitado = ISNULL((SELECT TOP 1 ro.Cantidad_Solicitado FROM APP.Ruta_Optima ro WHERE ro.Estatus_Registro_Id = 1 AND ro.Ruta_Optima_Id = @Ruta_Optima_Id),0);

SET @Cantidad_Entregado_Ubicacion_Ruta_Optima = ISNULL((SELECT TOP 1 SUM(ISNULL(ro.Cantidad_Entregado,0)) FROM APP.Ruta_Optima ro WHERE ro.Estatus_Registro_Id = 1 AND ro.Ruta_Optima_Id = @Ruta_Optima_Id),0);
SET @Cantidad_Entregado_Otra_Ubicacion = ISNULL((SELECT TOP 1 SUM(ISNULL(mus.Cantidad_Entregado,0)) FROM APP.[Material_Ubicacion_Almacenamiento_De_Surtido] mus WHERE mus.Estatus_Registro_Id = 1 AND mus.Ruta_Optima_Id = @Ruta_Optima_Id),0);

IF @Total_Stock_En_Ubicacion_Almacenamiento_Seleccionado < @Cantidad_Entregado
BEGIN
	SET @Cantidad_A_Entregar_Es_Valido = 0;	
	SET @Mensaje_Id = 1;
END
ELSE
IF (@Cantidad_Entregado + @Cantidad_Entregado_Ubicacion_Ruta_Optima + @Cantidad_Entregado_Otra_Ubicacion) <= @Cantidad_Solicitado
BEGIN
	SET @Cantidad_A_Entregar_Es_Valido = 1;	
END

IF @Cantidad_A_Entregar_Es_Valido = 0 AND @Mensaje_Id = 1
BEGIN
   SET @BusinessRules_PreventDefault = 1
   SET @Result_ShowViewMessage = '157 - ' + [dbo].[fn_Obtener_Mensaje_Sistema](157,1,0,'')
   SET @Result_ShowAlert = '157 - ' + [dbo].[fn_Obtener_Mensaje_Sistema](157,1,0,'')
   RETURN
END 
ELSE
IF @Cantidad_A_Entregar_Es_Valido = 0 AND @Mensaje_Id = 0
BEGIN
   SET @BusinessRules_PreventDefault = 1
   SET @Result_ShowViewMessage = '147 - ' + [dbo].[fn_Obtener_Mensaje_Sistema](147,1,0,'')
   SET @Result_ShowAlert = '147 - '+ [dbo].[fn_Obtener_Mensaje_Sistema](147,1,0,'')
   RETURN
END

DECLARE @Cantidad_A_Entregar_Es_Valido BIT = 0;
DECLARE @Mensaje_Id INT = 0;

DECLARE @Cantidad_Solicitado NUMERIC(15,4) = 0;
DECLARE @Cantidad_Entregado_Ubicacion_Ruta_Optima NUMERIC(15,4) = 0;
DECLARE @Cantidad_Entregado_Otra_Ubicacion NUMERIC(15,4) = 0;

SET @Cantidad_Solicitado = ISNULL((SELECT TOP 1 ro.Cantidad_Solicitado FROM APP.Ruta_Optima ro WHERE ro.Estatus_Registro_Id = 1 AND ro.Ruta_Optima_Id = @Ruta_Optima_Id),0);

SET @Cantidad_Entregado_Ubicacion_Ruta_Optima = ISNULL((SELECT TOP 1 SUM(ISNULL(ro.Cantidad_Entregado,0)) FROM APP.Ruta_Optima ro WHERE ro.Estatus_Registro_Id = 1 AND ro.Ruta_Optima_Id = @Ruta_Optima_Id),0);
SET @Cantidad_Entregado_Otra_Ubicacion = ISNULL((SELECT TOP 1 SUM(ISNULL(mus.Cantidad_Entregado,0)) FROM APP.[Material_Ubicacion_Almacenamiento_De_Surtido] mus WHERE mus.Estatus_Registro_Id = 1 AND mus.Ruta_Optima_Id = @Ruta_Optima_Id AND Material_Ubicacion_Almacenamiento_De_Surtido_Id <> @Material_Ubicacion_Almacenamiento_De_Surtido_Id),0);

IF @Total_Stock_En_Ubicacion_Almacenamiento_Seleccionado < @Cantidad_Entregado
BEGIN
	SET @Cantidad_A_Entregar_Es_Valido = 0;	
	SET @Mensaje_Id = 1;
END
ELSE
IF (@Cantidad_Entregado + @Cantidad_Entregado_Ubicacion_Ruta_Optima + @Cantidad_Entregado_Otra_Ubicacion) <= @Cantidad_Solicitado
BEGIN
	SET @Cantidad_A_Entregar_Es_Valido = 1;	
END

IF @Cantidad_A_Entregar_Es_Valido = 0 AND @Mensaje_Id = 1
BEGIN
   SET @BusinessRules_PreventDefault = 1
   SET @Result_ShowViewMessage =[dbo].[fn_Obtener_Mensaje_Sistema](157,1,0,'')
   SET @Result_ShowAlert = [dbo].[fn_Obtener_Mensaje_Sistema](157,1,0,'')
   RETURN
END 
ELSE
IF @Cantidad_A_Entregar_Es_Valido = 0 AND @Mensaje_Id = 0
BEGIN
   SET @BusinessRules_PreventDefault = 1
   SET @Result_ShowViewMessage =[dbo].[fn_Obtener_Mensaje_Sistema](147,1,0,'')
   SET @Result_ShowAlert = [dbo].[fn_Obtener_Mensaje_Sistema](147,1,0,'')
   RETURN
END
