DECLARE @Significa_Pendiente BIT = 0;
SET @Significa_Pendiente = ISNULL((SELECT TOP 1 es.Significa_Pendiente FROM APP.Solicitud s INNER JOIN APP.Estatus_Solicitud es ON s.Estatus_Solicitud_Id = es.Estatus_Solicitud_Id WHERE Solicitud_Id = @Solicitud_Id ),0)
IF @Significa_Pendiente = 1 AND ISNULL(@Motivo_Solicitud,'') = '' BEGIN SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = [dbo].[fn_Obtener_Mensaje_Sistema](168,1,0,'') SET @Result_ShowAlert = @Result_ShowViewMessage RETURN END ELSE IF @Significa_Pendiente = 1 AND ISNULL(@Motivo_Solicitud,'') <> '' BEGIN UPDATE APP.Solicitud SET Estatus_Registro_Id = 3 , Actualizado_Por_Id = @BusinessRules_UserId ,Fecha_Ultima_Actualizacion = dbo.fn_Convertir_UTC_A_Fecha_Hora_Local(GETDATE(),NULL) WHERE Solicitud_Id = @Solicitud_Id END ELSE IF @Significa_Pendiente = 0 BEGIN SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = [dbo].[fn_Obtener_Mensaje_Sistema](167,1,0,'') SET @Result_ShowAlert = @Result_ShowViewMessage RETURN END

