businessRules.rule:
SET @Fecha_Solicitud = dbo.fn_Convertir_UTC_A_Fecha_Hora_Local(GETDATE(),NULL);
SET @Solicitado_Por_Id = @BusinessRules_UserId;
SET @Tipo_Solicitud_Id = 2; -- Manual
SET @Estatus_Solicitud_Id = (SELECT TOP 1 Estatus_Solicitud_Id FROM APP.Estatus_Solicitud WHERE Significa_Pendiente = 1 AND Estatus_Registro_Id = 1);
SET @Registrado_Desde_Id = 9; -- Solicitud creado desde Solicitud Manual.

if([Cantidad_Solicitado]<=0){	
  	this.result.focus('Cantidad_Solicitado','Ingrese cantidad válido para solicitar.') 
    this.preventDefault();  	
}
else
if([Area_Entrega_Id]==0){
  	this.result.focus('Area_Entrega_Id','Seleccione área de entrega válida.') 
	this.preventDefault();
}
else
if ([Es_De_Excedente]==true && ([Orden_Produccion_Id] == 0 || [Orden_Produccion_Id]== null )){ 
this.result.focus('Orden_Produccion_Id','Seleccione orden de producción.') 
this.preventDefault();
}

DECLARE @Significa_Pendiente BIT = 0;
SET @Significa_Pendiente = ISNULL((SELECT TOP 1 es.Significa_Pendiente FROM APP.Solicitud s INNER JOIN APP.Estatus_Solicitud es ON s.Estatus_Solicitud_Id = es.Estatus_Solicitud_Id WHERE Solicitud_Id = @Solicitud_Id ),0)
IF @Significa_Pendiente = 0 BEGIN SET @BusinessRules_PreventDefault = 1 SET @Result_ShowViewMessage = [dbo].[fn_Obtener_Mensaje_Sistema](169,1,0,'') SET @Result_ShowAlert = @Result_ShowViewMessage RETURN END

var fieldValue = [Motivo_Solicitud];
if ([Motivo_Solicitud_Es_Otro] == true){
  if (fieldValue == null) {
      // prevent the default action processing
      this.preventDefault();
      // set the focus on the field and display an error
      this.result.focus('Motivo_Solicitud', 'Ingrese comentario, máximo 15 caracteres.');
  }
  else
    if (fieldValue.length > 15) {
      // prevent the default action processing
      this.preventDefault();
      // set the focus on the field and display an error
      this.result.focus('Motivo_Solicitud', 'Ingrese máximo 15 caracteres');
  }
}
